
window.onload=function(){window.setInterval(function(){var date=new Date();var days=date.getDate();var hours=date.getHours();var minutes=date.getMinutes();var seconds=date.getSeconds();if(days<10)days="0"+days;if(hours<10)hours="0"+hours;if(minutes<10)minutes="0"+minutes;if(seconds<10)seconds="0"+seconds;document.getElementById("days").innerHTML=days;document.getElementById("hours").innerHTML=hours;document.getElementById("minutes").innerHTML=minutes;document.getElementById("seconds").innerHTML=seconds;},1000);};const header=document.querySelector(".header__inner");window.addEventListener("scroll",function(){let pos=window.scrollY;if(pos>0){header.classList.add('background');}else{header.classList.remove('background');}});const smoothLinks=document.querySelectorAll('a[href^="#"]');for(let smoothLink of smoothLinks){smoothLink.addEventListener('click',function(e){e.preventDefault();const id=smoothLink.getAttribute('href');document.querySelector(id).scrollIntoView({behavior:'smooth',block:'start'});});};


const tabsBtn = document.querySelector(".burger");

tabsBtn.forEach(function(item) {
	item.addEventListener("click", function() {
		let currentBtn = item;
		
		currentBtn.classList.add('active');

	});
});